mapper.select:
select er.*,e.name ename,er.eid eid,d.name dname,p.name pname
from employeeremove er,employee e,department d,position p
where e.id=er.eid and er.afterDepId=d.id and er.afterJobId=p.id

  AND e.name LIKE CONCAT('%', #{empremove.employee.getName()}, '%')


  and er.afterdepid =#{empremove.department.getId()}


  and er.afterjobid =#{empremove.position.getId()}


  and er.removeDate between #{beginDateScope[0]} and #{beginDateScope[1]}


  limit #{page},#{size}

select count(*) from employeeremove er,employee e
where e.id=er.eid


    AND e.name LIKE CONCAT('%', #{empremove.employee.getName()}, '%')


    and er.afterdepid =#{empremove.department.getId()}


    and er.afterjobid =#{empremove.jobLevel.getId()}


    and ec.ecDate between #{beginDateScope[0]} and #{beginDateScope[1]}
